package SasiJava;

public class swapping {

	public static void main(String[] args) {
		int a =10;
		int b=20;
		System.out.println("before swap : ");
		System.out.println(a+" "+b);
		b = b+a;
		a=b-a;
		b=b-a;
		System.out.println("after swap : ");
		System.out.println(a+" "+b);

		// TODO Auto-generated method stub

	}

}
